package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Construtor de contatos completos. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class BuilderCompleto implements Builder {	/** Produto final. */	private ContatoCompleto contato = new ContatoCompleto();	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarNome(java.lang.String) */	public void adicionarNome(String nome) {		contato.setNome(nome);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEndereco(java.lang.String) */	public void adicionarEndereco(String endereco) {		contato.setEndereco(endereco);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarTelefone(java.lang.String) */	public void adicionarTelefone(String telefone) {		contato.setTelefone(telefone);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEmail(java.lang.String) */	public void adicionarEmail(String email) {		contato.setEmail(email);	}	/** Obt√©m o contato. */	public ContatoCompleto getContato() {		return contato;	}}