package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Construtor de contatos de Internet. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class BuilderInternet implements Builder {	/** Produto final. */	private ContatoInternet contato = new ContatoInternet();	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarNome(java.lang.String) */	public void adicionarNome(String nome) {		contato.setNome(nome);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEndereco(java.lang.String) */	public void adicionarEndereco(String endereco) {		/* Não faz nada! */	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarTelefone(java.lang.String) */	public void adicionarTelefone(String telefone) {		/* Não faz nada! */	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEmail(java.lang.String) */	public void adicionarEmail(String email) {		contato.setEmail(email);	}	/** Obtém o contato. */	public ContatoInternet getContato() {		return contato;	}}