package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Construtor de contatos telefônicos. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class BuilderTelefone implements Builder {	/** Produto final. */	private ContatoTelefone contato = new ContatoTelefone();	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarNome(java.lang.String) */	public void adicionarNome(String nome) {		contato.setNome(nome);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEndereco(java.lang.String) */	public void adicionarEndereco(String endereco) {		/* Não faz nada! */	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarTelefone(java.lang.String) */	public void adicionarTelefone(String telefone) {		contato.setTelefone(telefone);	}	/** @see br.ufes.inf.designpatterns.creational.builder.contatos.Builder#adicionarEmail(java.lang.String) */	public void adicionarEmail(String email) {		/* Não faz nada! */	}	/** Obtém o contato. */	public ContatoTelefone getContato() {		return contato;	}}