package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Representa um contato completo: nome, endere√ßo, telefone e e-mail. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class ContatoCompleto {	/** Nome. */	private String nome;	/** E-mail. */	private String endereco;	/** E-mail. */	private String telefone;	/** E-mail. */	private String email;	/** Setter for email. */	public void setEmail(String email) {		this.email = email;	}	/** Setter for endereco. */	public void setEndereco(String endereco) {		this.endereco = endereco;	}	/** Setter for nome. */	public void setNome(String nome) {		this.nome = nome;	}	/** Setter for telefone. */	public void setTelefone(String telefone) {		this.telefone = telefone;	}	/** @see java.lang.Object#toString() */	public String toString() {		return nome + ": " + endereco + " / " + telefone + " / " + email;	}}