package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Representa um contato da Internet: nome e e-mail. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class ContatoInternet {	/** Nome. */	private String nome;	/** E-mail. */	private String email;	/** Setter for email. */	public void setEmail(String email) {		this.email = email;	}	/** Setter for nome. */	public void setNome(String nome) {		this.nome = nome;	}	/** @see java.lang.Object#toString() */	public String toString() {		return nome + ": " + email;	}}