package br.ufes.inf.designpatterns.creational.builder.contatos;/** * Representa um contato de telefone: nome e telefone. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class ContatoTelefone {	/** Nome. */	private String nome;	/** Telefone. */	private String telefone;	/** Setter for nome. */	public void setNome(String nome) {		this.nome = nome;	}	/** Setter for telefone. */	public void setTelefone(String telefone) {		this.telefone = telefone;	}	/** @see java.lang.Object#toString() */	public String toString() {		return nome + ": " + telefone;	}}