package br.ufes.inf.designpatterns.creational.builder.patternburgers;import java.util.HashSet;import java.util.Set;/** * Classe que representa o pedido. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.01 */public class Pedido {	/** Produtos dentro da caixa. */	private Set<String> dentroDaCaixa = new HashSet<String>();	/** Produtos fora da caixa. */	private Set<String> foraDaCaixa = new HashSet<String>();	/** Adiciona um produto dentro da caixa. */	public void adicionarDentroDaCaixa(String item) {		dentroDaCaixa.add(item);	}	/** Adiciona um produto fora da caixa. */	public void adicionarForaDaCaixa(String item) {		foraDaCaixa.add(item);	}	/** @see java.lang.Object#toString() */	public String toString() {		StringBuffer buffer = new StringBuffer();		buffer.append("Seu pedido:\n");		buffer.append("Dentro da caixa:\n");		for (String item : dentroDaCaixa)			buffer.append("\t" + item + "\n");		buffer.append("Fora da caixa:\n");		for (String item : foraDaCaixa)			buffer.append("\t" + item + "\n");		buffer.append("\nTenha um bom dia!\n\n");		return buffer.toString();	}}