package br.ufes.inf.designpatterns.creational.factorymethod.log;/** * Classe que utiliza Factory Method para criar uma ferramenta de log e imprimir algo no log. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.03 */public abstract class EscrevedorLog {	/** Ferramenta de log. */	private FerramentaLog log;	/** Cria a ferramenta de log (método fábrica). */	protected abstract FerramentaLog criarFerramentaLog();	/** Escreve algo no log. */	public void escreverLog(String mensagem) {		if (log == null)			log = criarFerramentaLog();		log.registrarLog(mensagem);	}}