package br.ufes.inf.designpatterns.creational.factorymethod.log;import java.text.DateFormat;import java.text.SimpleDateFormat;/** * Classe abstrata que representa o produto: uma ferramenta de log. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.03 */public abstract class FerramentaLog {	/** Formatador de datas para anexar ao log. */	protected DateFormat dateFormat = new SimpleDateFormat("[dd.MM.yyyy, HH:mm] ");	/** Envia uma mensagem para o log. */	public abstract void registrarLog(String mensagem);}