package br.ufes.inf.designpatterns.creational.factorymethod.nomes;/** * Representa um nome. *  * @author Vitor E. Silva Souza (vitorsouza@gmail.com) * @version 2005.07.02 */public class Nome {	/** Primeiro nome. */	private String nome;	/** Sobrenome. */	private String sobrenome;	/** Construtor. */	public Nome(String nome, String sobrenome) {		this.nome = nome;		this.sobrenome = sobrenome;	}	/** @see java.lang.Object#toString() */	public String toString() {		return nome + " " + sobrenome;	}}